mapper.select:
SELECT /* BoardMapper.selectBoardList 게시판 조회 */ SEQ , CATEGORY , TITLE , BODY_TEXT , REG_ID , REG_DT , CNG_DT FROM MAIN_BOARD
AND SEQ = #{seq} AND TITLE LIKE #{title}
ORDER BY SEQ

mapper.insert:
INSERT /* BoardMapper.selectBoardList 게시판 조회 */
INTO MAIN_BOARD(
	SEQ
	, CATEGORY
	, TITLE
	, BODY_TEXT
	, REG_ID
	, REG_DT
	, CNG_DT
	)
VALUES(
	BOARD_SEQ.NEXTVAL
	, #{category}
	, #{title}
	, #{bodyText}
	, #{regId}
	, SYSDATE
	, SYSDATE
	)
