mapper.select:
SELECT /* BoardMapper.selectBoardListCnt 게시판 목록 갯수 조회 */
		COUNT(A.SEQ) AS TOTAL_CNT
  FROM MAIN_BOARD A 

	AND A.SEQ = #{seq}
	AND A.TITLE LIKE CONCAT('%',#{title},'%')
	AND A.CATEGORY = #{category}

SELECT CONCAT(FILE_NAME, '.', FILE_EXTENSION) AS FILE_NAME 
	   , FILE_EXTENSION
	   , FILE_DATA
  FROM BOARD_ADD_FILE
 WHERE SEQ = #{seq}
   AND FILE_SEQ = #{fileSeq}

mapper.insert:

	SELECT nextval(BOARD_SEQ)


INSERT /* BoardMapper.insertBoardOne 게시판 Insert */
INTO MAIN_BOARD(
	SEQ
	, CATEGORY
	, TITLE
	, BODY_TEXT
	, ORIGIN_BOARD_SEQ
	, ORDER_NUM
	, USER_NO
	, REG_DT
	, CNG_DT
	)
VALUES(
	#{boardSeq}
	, #{category}
	, #{title}
	, #{bodyText}
	, #{originBoardSeq}
	, #{orderNum}
	, #{userNo}
	, CURRENT_TIMESTAMP()
	, CURRENT_TIMESTAMP()
	)


	SELECT IFNULL(MAX(FILE_SEQ)+1 , '1') AS FILE_SEQ
	  FROM board_add_file
	 WHERE SEQ = #{boardSeq}


INSERT /* BoardMapper.insertBoardFile 게시판 첨부파일 Insert */
INTO BOARD_ADD_FILE(
	SEQ
	, FILE_SEQ
	, FILE_NAME
	, FILE_DATA
	, FILE_SIZE
	, FILE_EXTENSION
	, REG_DT
	, CNG_DT
	)
VALUES(
	#{boardSeq}
	, #{fileSeq}
	, #{fileName}
	, #{fileData}
	, #{fileSize}
	, #{fileExtension}
	, CURRENT_TIMESTAMP()
	, CURRENT_TIMESTAMP()
	)
