mapper.insert:
INSERT /* UserInfoMapper.insertUserInfoOne 사용자 정보 Insert */
INTO USER_INFO(
	USER_NO
	, HP_NO
	, EMAIL
	, ZONE_CODE
	, ROAD_ADDR
	, JIBUN_ADDR
	, DETAIL_ADDR
	, REG_ID
	, REG_DT
	, CNG_DT
	)
VALUES(
	, #{userNo}
	, #{hpNo}
	, #{email}
	, #{zoneCode}
	, #{roadAddr}
	, #{jibunAddr}
	, #{detailAddr}
	, #{regId}
	, CURRENT_TIMESTAMP()
	, CURRENT_TIMESTAMP()
	)

mapper.update:
UPDATE USER_INFO /* UserInfoMapper.updateUserInfoOne 사용자 정보 Update */
SET    HP_NO = #{hpNo}
	   , EMAIL = #{email}
	   , ZONE_CODE  = #{zoneCode}
       , ROAD_ADDR     = #{roadAddr}
       , JIBUN_ADDR = #{jibunAddr}
       , DETAIL_ADDR = #{detailAddr}
       , CNG_DT    = CURRENT_TIMESTAMP()
WHERE USER_NO = #{userNo}
